const { Database, AdvancedDatabase, CreateSchema } = require("./src/index");
const db = new Database({ path: "./esosdb/db.json", space: 2 });

const userSchemaProps = {
  name: { type: "string", required: true },
  age: { type: "number", required: true },
};

const adb = new AdvancedDatabase({ name: "ramco", space: 2 });

const User = new CreateSchema({
  connect: adb,
  name: "user",
  props: {
    name: { type: "string", required: true },
    age: { type: "number", required: false },
  },
  timestamps: true,
});

const Group = new CreateSchema({
  connect: adb,
  name: "group",
  props: {
    users: { type: "string[]", required: true },
    position: { type: "string", required: true },
  },
  timestamps: true,
});

Group.on("ready", (data) => {
  console.log(data);
});

User.on("ready", (data) => console.log(data));

User.deleteById("0bcb1b66-1f7e-4e78-9771-19f425f9be90");
